mapper.select:
SELECT d."id" AS "id", d."name" AS "name", d."sex" AS "sex", d."tel" AS "tel", d."school" AS "school", d."degree" AS "degree", d."job" AS "job", d."recommended" AS "recommended", d."status" AS "status", md."name" AS "deptName", mds."name" AS "subName" FROM HOSPITAL.DOCTOR d JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR mdsd ON mdsd."doctor_id" = d."id" JOIN HOSPITAL.MEDICAL_DEPT_SUB mds ON mdsd."dept_sub_id" = mds."id" JOIN HOSPITAL.MEDICAL_DEPT md ON mds."dept_id" = md."id"
WHERE 1=1 AND d."status" = ${status}
AND d."name" LIKE '%${name}%'
AND d."degree" = #{degree}
AND d."job" = #{job}
AND d."recommended" = ${recommended}
AND md."id" = ${deptId}
ORDER BY md."id" ${order}
LIMIT ${length} OFFSET #{start}

SELECT COUNT(*)
FROM HOSPITAL.DOCTOR d
JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR mdsd ON mdsd."doctor_id" = d."id"
JOIN HOSPITAL.MEDICAL_DEPT_SUB mds ON mdsd."dept_sub_id" = mds."id"
JOIN HOSPITAL.MEDICAL_DEPT md ON mds."dept_id" = md."id"

WHERE 1=1
AND d."status" = ${status}

	AND d."name" LIKE '%${name}%'


	AND d."degree" = #{degree}


	AND d."job" = #{job}


	AND d."recommended" = ${recommended}


	AND md."id" = ${deptId}
